    const int mod = 1e9 + 7;

    class Matrix &#123;
        public:
            int row, col;
            int a[105][105];

            Matrix() &#123;
                memset(a, 0, sizeof(a));
            &#125;

            Matrix(int x, int y) &#123;
                row = x;
                col = y;
                memset(a, 0, sizeof(a));
            &#125;

            friend istream &amp; operator&gt;&gt;(istream &amp;is, Matrix &amp;x) &#123;
                for (int i = 1; i &lt;= x.row; i++) &#123;
                    for (int j = 1; j &lt;= x.col; j++) &#123;
                        is &gt;&gt; x.a[i][j];
                    &#125;
                &#125;
                return is;
            &#125;

            friend ostream &amp; operator&lt;&lt;(ostream &amp;os, const Matrix &amp;x) &#123;
                for (int i = 1; i &lt;= x.row; i++) &#123;
                    for (int j = 1; j &lt;= x.col; j++) &#123;
                        os &lt;&lt; x.a[i][j] &lt;&lt; &#39; &#39;;
                    &#125;
                    if (i != x.row) &#123;
                        os &lt;&lt; &#39;\n&#39;;
                    &#125;
                &#125;
                return os;
            &#125;

            Matrix operator*(const Matrix &amp; x) &#123;
                Matrix res(row, x.col);
                for (int i = 1; i &lt;= row; i++) &#123;
                    for (int k = 1; k &lt;= col; k++) &#123;
                        int tmp = a[i][k];
                        for (int j = 1; j &lt;= x.col; j++) &#123;
                            (res.a[i][j] += tmp * x.a[k][j]) %= mod;
                        &#125;
                    &#125;
                &#125;
                return res;
            &#125;

            void operator*=(const Matrix &amp; x) &#123;
                (*this) = (*this) * x;
            &#125;

            void operator%=(const int &amp;x __attribute__((unused))) &#123;&#125;
    &#125;;

    template&lt;typename T&gt;
    T ksm(T x, int y) &#123;
        T res = x;
        y--;
        while (y) &#123;
            if (y &amp; 1) &#123;
                res *= x;
            &#125;
            x *= x;
            y &gt;&gt;= 1;
        &#125;
        return res;
    &#125;
&#125;

cpp#include&lt;bits/stdc++.h&gt;
using namespace std;
const int maxn = 5e5 + 5;

int tot, ccnt;
int deg[maxn];
unordered_map&lt;string, int&gt; col;
vector&lt;int&gt; p[maxn];

struct DSU {
    int fa[maxn];
    DSU() {
        for (int i = 1; i &lt; maxn; i++) {
            fa[i] = i;
        }
    }
    int getfa(int x) {
        if (fa[x] == x) {
            return x;
        } else {
            return fa[x] = getfa(fa[x]);
        }
    }

    bool merge(int x, int y) {
        int fx = getfa(x), fy = getfa(y);
        if (fx == fy) {
            return 0;
        }
        fa[fx] = fy;
        return 1;
    }
} dsu;

int main() {
    ios::sync_with_stdio(0);
    cin.tie(0);
    string s, t;
    while (cin &gt;&gt; s &gt;&gt; t) {
        if (!col[s]) {
            col[s] = ++tot;
        }
        if (!col[t]) {
            col[t] = ++tot;
        }
        if (dsu.merge(col[s], col[t])) {
            ccnt++;
        }
        p[col[s]].push_back(col[t]);
        p[col[t]].push_back(col[s]);
        deg[col[s]]++, deg[col[t]]++;
    }
    if (ccnt &lt; tot - 1) {
        cout &lt;&lt; "Impossible\n";
        return 0;
    }
    int cnt = 0;
    for (int i = 1; i &lt;= tot; i++) {
        if ((deg[i] &amp; 1) &amp;&amp; ++cnt &gt; 2) {
            cout &lt;&lt; "Impossible\n";
            return 0;
        }
    }
    cout &lt;&lt; "Possible\n";
    return 0;
}
