content:
前言pb_ds 库是 gnu 内置的一个功能强大的库，封装了许多常用但代码量大的数据结构，例如 红黑树、Splay、可并堆…
需要新引入的头文件和命名空间：
cpp#include&lt;bits/extc++.h&gt;
using namespace __gnu_cxx;
using namespace __gnu_pbds;（注意：Clang++中没有此扩展库，需要使用g++编译器，并且不能是TDM-GCC）
堆声明cpptemplate&lt;class _Tv, class Cmp_Fn = std::less&lt;__gnu_pbds::priority_queue&lt;_Tv, Cmp_Fn, Tag, _Alloc&gt;::value_type&gt;, class Tag = __gnu_pbds::pairing_heap_tag, class _Alloc = std::allocator&lt;char&gt;&gt; class __gnu_pbds::priority_queue&lt;_Tv, Cmp_Fn, Tag, _Alloc&gt;解析：

_Tv：堆中元素的类型
Cmp_Fn：比较函数，默认为less，于std::priority_queue相同，为大根堆
Tag：默认即可
_Alloc：别管

定义定义一个存整数的小根堆：
cpp__gnu_pbds::priority_queue&lt;int,greater&lt;int&gt;&gt; q;（为于STL区分开，此处应指定命名空间__gnu_pbds::）
用法区别于STL的实用用法：
push：pb_ds中的push函数具有返回值，返回被push元素的迭代器
modify：传入(迭代器，新值)，将迭代器所指向的元素变成新的值
join：用法形如a.join(b)，将b堆合并到a堆（需要两个堆定义相同），并清空b堆
例题洛谷P3377 【模板】左偏树&#x2F;可并堆
通过代码（O2：212ms）：
cpp#include&lt;bits/stdc++.h&gt;
#include&lt;bits/extc++.h&gt;
using namespace std;
using namespace __gnu_cxx;
using namespace __gnu_pbds;
const int maxn = 1e5 + 5;

int n, m;
int d[maxn];
int fa[maxn];
__gnu_pbds::priority_queue&lt;pair&lt;int, int&gt;, greater&lt;pair&lt;int, int&gt;&gt;&gt; q[maxn];

int getfa(int x) &#123;
    if (fa[x] == x) &#123;
        return x;
    &#125; else &#123;
        return fa[x] = getfa(fa[x]);
    &#125;
&#125;

int main() &#123;
    ios::sync_with_stdio(0);
    cin.tie(0);
    cin &gt;&gt; n &gt;&gt; m;
    for (int i = 1, x; i &lt;= n; i++) &#123;
        cin &gt;&gt; x;
        q[i].push(&#123;x, i&#125;);
        fa[i] = i;
    &#125;
    for (int i = 1, opt, x, y; i &lt;= m; i++) &#123;
        cin &gt;&gt; opt;
        if (opt == 1) &#123;
            cin &gt;&gt; x &gt;&gt; y;
            if (d[x] || d[y]) &#123;
                continue;
            &#125;
            int fx = getfa(x), fy = getfa(y);
            if (fx == fy) &#123;
                continue;
            &#125;
            fa[fx] = fy;
            q[fy].join(q[fx]);
        &#125; else &#123;
            cin &gt;&gt; x;
            if (d[x]) &#123;
                cout &lt;&lt; -1 &lt;&lt; &#39;\n&#39;;
                continue;
            &#125;
            int ff = getfa(x);
            cout &lt;&lt; q[ff].top().first &lt;&lt; &#39;\n&#39;;
            d[q[ff].top().second] = 1;
            q[ff].pop();
        &#125;
    &#125;
    return 0;
&#125;未完待续…

Tips
支持直接 cin, cout

Codecppnamespace Badnuker &#123;
    const int mod = 1e9 + 7;

    class Matrix &#123;
        public:
            int row, col;
            int a[105][105];

            Matrix() &#123;
                memset(a, 0, sizeof(a));
            &#125;

            Matrix(int x, int y) &#123;
                row = x;
                col = y;
                memset(a, 0, sizeof(a));
            &#125;

            friend istream &amp; operator&gt;&gt;(istream &amp;is, Matrix &amp;x) &#123;
                for (int i = 1; i &lt;= x.row; i++) &#123;
                    for (int j = 1; j &lt;= x.col; j++) &#123;
                        is &gt;&gt; x.a[i][j];
                    &#125;
                &#125;
                return is;
            &#125;

            friend ostream &amp; operator&lt;&lt;(ostream &amp;os, const Matrix &amp;x) &#123;
                for (int i = 1; i &lt;= x.row; i++) &#123;
                    for (int j = 1; j &lt;= x.col; j++) &#123;
                        os &lt;&lt; x.a[i][j] &lt;&lt; &#39; &#39;;
                    &#125;
                    if (i != x.row) &#123;
                        os &lt;&lt; &#39;\n&#39;;
                    &#125;
                &#125;
                return os;
            &#125;

            Matrix operator*(const Matrix &amp; x) &#123;
                Matrix res(row, x.col);
                for (int i = 1; i &lt;= row; i++) &#123;
                    for (int k = 1; k &lt;= col; k++) &#123;
                        int tmp = a[i][k];
                        for (int j = 1; j &lt;= x.col; j++) &#123;
                            (res.a[i][j] += tmp * x.a[k][j]) %= mod;
                        &#125;
                    &#125;
                &#125;
                return res;
            &#125;

            void operator*=(const Matrix &amp; x) &#123;
                (*this) = (*this) * x;
            &#125;

            void operator%=(const int &amp;x __attribute__((unused))) &#123;&#125;
    &#125;;

    template&lt;typename T&gt;
    T ksm(T x, int y) &#123;
        T res = x;
        y--;
        while (y) &#123;
            if (y &amp; 1) &#123;
                res *= x;
            &#125;
            x *= x;
            y &gt;&gt;= 1;
        &#125;
        return res;
    &#125;
&#125;

cpp#include&lt;bits/stdc++.h&gt;
using namespace std;
const int maxn = 5e5 + 5;

int tot, ccnt;
int deg[maxn];
unordered_map&lt;string, int&gt; col;
vector&lt;int&gt; p[maxn];

struct DSU {
    int fa[maxn];
    DSU() {
        for (int i = 1; i &lt; maxn; i++) {
            fa[i] = i;
        }
    }
    int getfa(int x) {
        if (fa[x] == x) {
            return x;
        } else {
            return fa[x] = getfa(fa[x]);
        }
    }

    bool merge(int x, int y) {
        int fx = getfa(x), fy = getfa(y);
        if (fx == fy) {
            return 0;
        }
        fa[fx] = fy;
        return 1;
    }
} dsu;

int main() {
    ios::sync_with_stdio(0);
    cin.tie(0);
    string s, t;
    while (cin &gt;&gt; s &gt;&gt; t) {
        if (!col[s]) {
            col[s] = ++tot;
        }
        if (!col[t]) {
            col[t] = ++tot;
        }
        if (dsu.merge(col[s], col[t])) {
            ccnt++;
        }
        p[col[s]].push_back(col[t]);
        p[col[t]].push_back(col[s]);
        deg[col[s]]++, deg[col[t]]++;
    }
    if (ccnt &lt; tot - 1) {
        cout &lt;&lt; "Impossible\n";
        return 0;
    }
    int cnt = 0;
    for (int i = 1; i &lt;= tot; i++) {
        if ((deg[i] &amp; 1) &amp;&amp; ++cnt &gt; 2) {
            cout &lt;&lt; "Impossible\n";
            return 0;
        }
    }
    cout &lt;&lt; "Possible\n";
    return 0;
}
